<?php

/**
 * Load Fonts
 */
function festival_of_trees_load_fonts() {

	wp_register_style( 'et-googleFonts', 'http://fonts.googleapis.com/css?family=Lato:400,700' );
	wp_enqueue_style( 'et-googleFonts' );

} // festival_of_trees_load_fonts()
add_action( 'wp_print_styles', 'festival_of_trees_load_fonts' );



/**
 * Prints whatever in a nice, readable format
 */
function pretty( $input ) {

	echo '<pre>'; print_r( $input ); echo '</pre>';

} // pretty()


/**
 * Add Down Caret to Menus with Children
 *
 * @param 	string 		$item_output		//
 * @param 	object 		$item				//
 * @param 	int 		$depth 				//
 * @param 	array 		$args 				//
 *
 * @return 	string 							modified menu
 */
function festival_of_trees_menu_caret( $item_output, $item, $depth, $args ) {

	if ( ! in_array( 'menu-item-has-children', $item->classes ) ) { return $item_output; }

	$output = '<a href="' . $item->url . '">';
	$output .= $item->title;
	$output .= '<span class="children">' . festival_of_trees_get_svg( 'caret-down' ) . '</span>';
	$output .= '</a>';

	return $output;

} // festival_of_trees_menu_caret()
add_filter( 'walker_nav_menu_start_el', 'festival_of_trees_menu_caret', 10, 4 );



/**
 * Change Social Menu to Icons Only
 *
 * @link 	http://www.billerickson.net/customizing-wordpress-menus/
 *
 * @param 	string 		$item_output		//
 * @param 	object 		$item				//
 * @param 	int 		$depth 				//
 * @param 	array 		$args 				//
 *
 * @return 	string 							modified menu
 */
function festival_of_trees_social_menu_svgs( $item_output, $item, $depth, $args ) {

	if ( 'social' !== $args->theme_location ) { return $item_output; }

	$host 	= parse_url( $item->url, PHP_URL_HOST );
	$output = '<a href="' . $item->url . '" class="icon-menu">';
	$class 	= festival_of_trees_get_svg_by_class( $item->classes );

	if ( ! empty( $class ) ) {

		$output .= $class;

	} else {

		$output .= festival_of_trees_get_svg_by_url( $item->url );

	} // class check

	$output .= '</a>';

	return $output;

} // festival_of_trees_social_menu_svgs()
add_filter( 'walker_nav_menu_start_el', 'festival_of_trees_social_menu_svgs', 10, 4 );



/**
 * Change Social Menu to Icons Only
 *
 * @link 	http://www.billerickson.net/customizing-wordpress-menus/
 *
 * @param 	string 		$item_output		//
 * @param 	object 		$item				//
 * @param 	int 		$depth 				//
 * @param 	array 		$args 				//
 *
 * @return 	string 							modified menu
 */
function festival_of_trees_header_menu_svgs( $item_output, $item, $depth, $args ) {

	if ( 'header' !== $args->theme_location ) { return $item_output; }

	$host 	= parse_url( $item->url, PHP_URL_HOST );
	$output = '<a href="' . $item->url . '" class="icon-menu">';
	$class 	= festival_of_trees_get_svg_by_class( $item->classes );

	if ( ! empty( $class ) ) {

		$output .= $class;

	} else {

		$output .= festival_of_trees_get_svg_by_url( $item->url );

	} // class check

	$output .= '<div class="menu-icon-label">' . $item->title . '</div>';
	$output .= '</a>';

	return $output;

} // festival_of_trees_header_menu_svgs()
add_filter( 'walker_nav_menu_start_el', 'festival_of_trees_header_menu_svgs', 10, 4 );





function festival_of_trees_add_search_form_to_menu( $items, $args ) {

	if ( 'header' !== $args->theme_location ) { return $items; }

	// On main menu: put styling around search and append it to the menu items
	$output = '<li class="search menu-item">';
	$output .= '<a href="#" class="icon-menu">';
	$output .= festival_of_trees_get_svg( 'search' );
	$output .= '<div class="menu-icon-label">Search</div>';
	$output .= '</a>';
	$output .= get_search_form( false );
	$output .= '</li>' . $items;

	return $output;

} // festival_of_trees_add_search_form_to_menu()
add_filter('wp_nav_menu_items', 'festival_of_trees_add_search_form_to_menu', 10, 2);



/**
 * Gets the appropriate SVG based on a menu item class
 *
 * @param  [type] $url [description]
 * @return [type]      [description]
 */
function festival_of_trees_get_svg_by_class( $classes ) {

	$output = '';

	foreach ( $classes as $class ) {

		$check = festival_of_trees_get_svg( $class );

		if ( ! is_null( $check ) ) { $output .= $check; break; }

	} // foreach

	return $output;

} // festival_of_trees_get_svg_by_class()

